//计算时间之差
function diffDate(startTime, endTime, diffType) {
    //将时间格式转换为可读的格式
    startTime = startTime.replace(/\-/g, "/");
    endTime = endTime.replace(/\-/g, "/");

    //将计算间隔类性字符转换为小写
    diffType = diffType.toLowerCase();
    var sTime = new Date(startTime);      //开始时间
    var eTime = new Date(endTime);  //结束时间
    //作为除数的数字
    var dNum = 1;
    switch (diffType) {
        case "s":
            dNum = 1000;
            break;
        case "m":
            dNum = 1000 * 60;
            break;
        case "h":
            dNum = 1000 * 3600;
            break;
        case "d":
            dNum = 1000 * 3600 * 24;
            break;
        default:
            break;
    }
    return parseInt((eTime.getTime() - sTime.getTime()) / parseInt(dNum));
}
//给YYYY-MM-dd加天数
function DateAdd(sdate, days) {
    var a = new Date(sdate);
    a = a.valueOf();
    a = a + days * 24 * 60 * 60 * 1000;
    a = new Date(a);
    var Y=a.getFullYear();
    var M=a.getMonth()+1;
    var D=a.getDate();
    if (M < 10) {
        M = "0" + M;
    }
    if (D < 10) {
        D= "0" + D;
    }
    date=Y+"-"+M+"-"+D;
    return date;
}
$(function(){
    //限制旅游景点字数
    $(".guide ul li a p").each(function(){
        var textNum=35;
        if($(this).text().length>textNum){
            $(this).text($(this).text().substring(0,textNum));
            $(this).html($(this).html()+'...');
        }
    });
    //热门车型侧栏导航选中
    $(".car-nav ul li").click(function(){
        $(this).addClass("selected").siblings().removeClass("selected");
    });
    //搜索框选项卡
    $(".sosuo-top ul li").click(function(){
        $(this).addClass("on").siblings().removeClass("on");

    });
    //登陆弹出
    $("#login").click(function(){
        $(".login").fadeIn();
    });
    $(".login-close").click(function(){
        $(".login").fadeOut();
    });
    $(".login").click(function(){
        $(this).fadeOut();
    });
    $(".login-box").click(function(e)
    {
        e.stopPropagation();
    });

    //用户协议弹窗
    $(".agree span").click(function(){
        $(".agree-bg").fadeIn();
    });
    $(".agree-title span").click(function(){
        $(".agree-bg").fadeOut();
    });
    //切换时间范围
    $(function () {
        $(".sosuo-top ul li").click(function () {
            var dateIndex = $(this).index();
            if (dateIndex == 0) {
                $(".sousuo-bg .form-box").animate({left:0});
                $(".days span").html("0");
            } else if (dateIndex == 1) {
                $(".sousuo-bg .form-box").animate({left:-480+"px"});
                $(".days span").html("5");
            }
            else if (dateIndex == 2) {
                $(".sousuo-bg .form-box").animate({left:-960+"px"});
                $(".days span").html("20");
            }
        });
    });
});
//回到顶部
$(window).scroll(function () {
    var winH=$(this).height();
    var scrollH=$(this).scrollTop();
    if(scrollH>winH) {
        $("#back-top").stop().animate({bottom:180+"px"});
    }
    else {
        $("#back-top").stop().animate({bottom: -50 + "px"});
    }
});
$(function () {
    //click为事件名，不需要改
    //.curr_page表示class名，后面的a表示在这个class的div中给a标记添加click方法
    $(document).on("click",".curr-page a",function(){

        var pageOBJ = this;
        var url = pageOBJ.href;
        $.ajax({
            type:'get',
            url:url,
            success:function (result) {
                //main是class名，即显示数据的这个div的class名
                if ($('div').hasClass('news-page')) {
                    $(".news-page").html(result);
                } else {
                    $(".fav-main").html(result);
                    $(".rental-list-right").html(result);
                    $(".mod").html(result);
                }

//                    console.log(result);
            }
        });

        return false;
    })
});